#!/usr/bin/env bash
# GNU/Linux startup script generated by JavaPackager plugin

SCRIPTPATH=$(dirname $(readlink -e "$0"))

JAVA="$SCRIPTPATH/jre/bin/java"

BINARY="$SCRIPTPATH/fonty"


JVMDefaultOptions=""
JVMOptionsFile="$SCRIPTPATH/fonty.l4j.ini"
[ -f $JVMOptionsFile ] && while read -r option; do JVMDefaultOptions+=" $option"; done <<< $(sed "/^#.*$/d" $JVMOptionsFile)

JVMClassPath="$BINARY"

cd "$SCRIPTPATH"

$JAVA $JVMDefaultOptions -cp $JVMClassPath com.dimitris47.fonty.Launcher $@
exit 0
